//SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;


import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/access/AccessControl.sol';
import '@openzeppelin/contracts/utils/escrow/Escrow.sol';
import '@openzeppelin/contracts/utils/math/SafeMath.sol';


/**
 * @title Payment gateway using OpenZeppelin Escrow
 * @author Alexandre
 */
contract PaymentGateway is Ownable, AccessControl {

    // Create a new role identifier for the escrow role
    bytes32 public constant ESCROW_ROLE = keccak256("ESCROW_ROLE");


    using SafeMath for uint256;

    Escrow escrow;
    address payable wallet;

    /**
     * @param _wallet receives funds from the payment gateway
     */
    constructor(address payable _wallet) { //public 
        escrow = new Escrow();
        wallet = _wallet;
        _setupRole(ESCROW_ROLE, msg.sender);
    }

    /**
     * Receives payments from customers
     */
    function sendPayment() external payable {
        //escrow.deposit.value(msg.value)(wallet);
        require(hasRole(ESCROW_ROLE, msg.sender), "Caller is not a escrow");
        escrow.deposit{value:msg.value}(wallet);
    }

    /**
     * Withdraw funds to wallet
     */
    function withdraw() external onlyOwner {
        escrow.withdraw(wallet);
        //require(hasRole(MINTER_ROLE, msg.sender), "Caller is not a minter");
    }

    /**
     * Checks balance available to withdraw
     * @return the balance
     */
    function balance() external view returns (uint256) { //onlyOwner
        require(hasRole(ESCROW_ROLE, msg.sender), "Caller is not a escrow");
        return escrow.depositsOf(wallet);
    }
}
